<?php/** * Demonstrates declaration and use of variables and constants. * * Output is wrapped in html using the template methods *html* * and *display_html()*. * * @author Philip Windridge <p.c.windridge@staffs.ac.uk> * * @see /shared/template.php Documentation of html(), and * display_html() *//** * Include template.php to provide HTML wrapping and output */include __DIR__ . '/../shared/template.php';$bodySections = array(    getHtmlSection(        'header',        getHtmlSection('h1', 'Selection')    ),	getHtmlSection(		'p',		'For a full list of selection statements see \'Control Structures\' at: ' .		htmlLink('http://php.net/manual/en/language.control-structures.php')	));$ifStatement = 'The ' . code('if') . ' statement has a similar structure to that of C (' .    code('expr') . ' is a condition that equates to ' . code('True') . ' or ' . code('False') . '):' .    codeBlock(        array(            code('if (expr)'),            code("statement", 1)        )    ) .    'The following code shows the ' . code('if') . ' statement in use:' .    codeBlock(        array(            code('<?php'),            code('if ($wellFed and $cat) {'),            code('echo \'The cat is well fed!\';', 1),            code('}')        )    );$elseStatement = 'The ' . code('else') . ' provides the option to execute code if the ' . code('if') .	' statement condition isn\'t met.' .	codeBlock(		array(			code('if (expr)'),			code("statement", 1),			code('else'),			code('statement', 1)		)	) .	'The following code shows the ' . code('else') . ' statement in use:' .	codeBlock(		array(			code('<?php'),			code('if ($wellFed and $cat) {'),			code('echo \'The cat is well fed!\';', 1),			code('} else {'),			code('echo \'The cat is hungry!\'', 1),			code('}')		)	);$ifElseifStatement = 'The ' . code('elseif') . ' ( can also use ' . code('else if') .	') statement extends an ' . code('if') . ' statement:' .    codeBlock(        array(            code('if (expr)'),            code('statement', 1),            code('elseif (expr)'),            code('statement', 1)        )    ) .    'The following code shows the ' . code('elseif') . ' statement in use:' .    codeBlock(        array(            code('<?php'),            code('if ($wellFed and $cat) {'),            code('echo \'The cat is well fed!\';', 1),            code('} elseif ($wellFed and $dog) {'),            code('echo \'The dog is still hungry!\';', 1),			code('} else {'),			code('echo \'Your pet is hungry!\'', 1),			code('}')        )    );$switchStatement = 'The switch statement allows a series of conditional tests comparing an expression' .	' with a series of comparison values:' .    codeBlock(        array(            code('switch (expr)'),            code('case comparisonValue1:', 1),            code('statement', 2),            code('case comparisonValue2:', 1),            code('statement', 2),            code('default:', 1),            code('statement', 2)        )    ) .    'The following code shows the ' . code('switch') . ' statement in use:' .    codeBlock(        array(            code('$numberOfMeals = 2;'),            code('switch ($numberOfMeals) {'),            code('case 2:', 1),            code('echo \'Cat is well fed but dog is still hungry!\';', 2),            code('break;', 2),            code('case 1:', 1),            code('echo \'Both cat and dog are hungry!\';', 2),            code('break;', 2),            code('default:', 1),            code('echo \'This is just not a good state of affairs in terms of feeding your pet!\';', 2),            code('}')        )    ) .    'The ' . code('break') . ' statement ends the execution of the current code block. If the ' .	code('break') . ' statement occurs inside a loop then it will exit the loop. This is' .	' different to the ' . code('continue') . ' statement which, if used in a loop, ends the current iteration' .	' and continues at the condition evaluation.' .    ' If there is no ' . code('break') . ' statement then execution will continue to the next case match:' .    codeBlock(        array(            code('$numberOfMeals = 5;'),            code('switch ($numberOfMeals) {'),            code('case 5:', 1),            code('echo \'Both cat and dog are well fed!\';', 2),            code('break;', 2),            code('case 4:', 1),            code('case 3:', 1),            code('case 2:', 1),            code('echo \'Cat is well fed but dog is still hungry!\';', 2),            code('break;', 2),            code('case 1:', 1),            code('echo \'Both cat and dog are hungry!\';', 2),            code('break;', 2),            code('default:', 1),            code('echo \'This is just not a good state of affairs in terms of feeding your pet!\';', 2),            code('}')        )    ) .    'The ' . code('default') . ' case is an optional \'catch-all\'.' .	' Use it to execute code where no specified case has been matched.';$bodySections[] = getHtmlSection(    'dl',    getHtmlSection('dt', code('if') . ' Statement') .    getHtmlSection('dd', $ifStatement) .    getHtmlSection('dt', code('else') . ' Statement') .	getHtmlSection('dd', $elseStatement) .	getHtmlSection('dt', code('elseif') . ' Statement') .    getHtmlSection('dd', $ifElseifStatement) .    getHtmlSection('dt', code('switch') . ' Statement') .    getHtmlSection('dd', $switchStatement));displayHtml('Selection', $bodySections);